import React from "react";
import { Modal, Typography, Tag, Divider, Image, Space, Avatar } from "antd";
import dayjs from "dayjs";
import { UserOutlined, CalendarOutlined, TagOutlined } from "@ant-design/icons";

const { Title, Paragraph, Text } = Typography;

const BlogView = ({ blog, isVisible, onClose }) => {
  if (!blog) return null;

  return (
    <Modal
      title={<Title level={4}>Blog Details</Title>}
      open={isVisible}
      onCancel={onClose}
      footer={null}
      width={800}
      bodyStyle={{ maxHeight: "70vh", overflow: "auto" }}
    >
      <div className="blog-view">
        <Title level={2}>{blog.title}</Title>
        
        <Space className="mb-4" size={16}>
          <Space>
            <Avatar icon={<UserOutlined />} />
            <Text type="secondary">{blog.author}</Text>
          </Space>
          <Space>
            <CalendarOutlined />
            <Text type="secondary">{dayjs(blog.date).format("MMMM D, YYYY")}</Text>
          </Space>
        </Space>

        <div className="mb-4">
          <Space>
            <Text type="secondary">Status:</Text>
            <Tag color={blog.status === "published" ? "green" : "orange"}>
              {blog.status === "published" ? "Published" : "Draft"}
            </Tag>
          </Space>
        </div>

        {blog.image && (
          <div className="mb-6">
            <Image
              src={blog.image}
              alt={blog.title}
              style={{ maxWidth: "100%", maxHeight: "400px", objectFit: "cover" }}
              fallback="data:image/png;base64,"
            />
          </div>
        )}

        {blog.excerpt && (
          <div className="mb-6">
            <Title level={5}>Excerpt</Title>
            <Paragraph className="text-gray-600 italic">
              {blog.excerpt}
            </Paragraph>
            <Divider />
          </div>
        )}

        <div className="mb-6">
          <Title level={5}>Content</Title>
          <div 
            className="blog-content" 
            dangerouslySetInnerHTML={{ __html: blog.content }}
          />
        </div>

        {blog.categories && blog.categories.length > 0 && (
          <div className="mb-4">
            <Title level={5}>Categories</Title>
            <div>
              {blog.categories.map((category) => (
                <Tag color="blue" key={category._id || category.name}>
                  {category.name}
                </Tag>
              ))}
            </div>
          </div>
        )}

        {blog.tags && blog.tags.length > 0 && (
          <div className="mb-4">
            <Title level={5}>Tags</Title>
            <div>
              {blog.tags.map((tag) => (
                <Tag color="purple" key={tag._id || tag.name}>
                  {tag.name}
                </Tag>
              ))}
            </div>
          </div>
        )}

        {blog.seoTitle && (
          <div className="mb-4">
            <Title level={5}>SEO Title</Title>
            <Paragraph>{blog.seoTitle}</Paragraph>
          </div>
        )}

        {blog.metaKeywords && (
          <div>
            <Title level={5}>Meta Keywords</Title>
            <Paragraph>{blog.metaKeywords}</Paragraph>
          </div>
        )}
      </div>
    </Modal>
  );
};

export default BlogView;